import { apiRequest } from "./queryClient";

export interface AuthResponse {
  user: {
    id: number;
    username: string;
    email?: string;
    fullName?: string;
    companyName?: string;
    role: string;
    status?: string;
  };
  type?: string;
}

export interface RegistrationData {
  fullName: string;
  companyName: string;
  email: string;
  phone: string;
  password: string;
  confirmPassword: string;
}

export interface CustomerLoginData {
  email: string;
  password: string;
  rememberMe?: boolean;
}

export interface EmployeeLoginData {
  username: string;
  password: string;
}

export interface ApiError {
  message: string;
}

export interface Ticket {
  id: number;
  ticketId: string;
  subject: string;
  description: string;
  status: 'open' | 'in_progress' | 'pending' | 'resolved' | 'closed';
  priority: 'low' | 'medium' | 'high' | 'critical';
  userId?: number;
  adUserId?: number;
  assignedTo?: number;
  createdAt: Date;
  updatedAt: Date;
  lastUpdated: Date;
}

export interface Subscription {
  id: number;
  userId: number;
  name: string;
  description?: string;
  status: string;
  renewalDate?: Date;
  licenseType?: string;
  subscriptionId: string;
}

export interface ApplicationLink {
  id: number;
  name: string;
  url: string;
  description?: string;
  icon: string;
  isActive: boolean;
  order: number;
}

export interface TicketStats {
  total: number;
  open: number;
  resolved: number;
  highPriority: number;
}

export interface UserForApproval {
  id: number;
  username: string;
  email: string;
  fullName: string;
  companyName?: string;
  phone?: string;
  createdAt: Date;
}

// Authentication API
export const authApi = {
  // Register a new customer
  register: async (data: RegistrationData): Promise<{ message: string }> => {
    const response = await apiRequest('POST', '/api/auth/register', data);
    return response.json();
  },

  // Login as a customer
  customerLogin: async (data: CustomerLoginData): Promise<AuthResponse> => {
    const response = await apiRequest('POST', '/api/auth/login', data);
    return response.json();
  },

  // Login as an employee using AD credentials
  employeeLogin: async (data: EmployeeLoginData): Promise<AuthResponse> => {
    const response = await apiRequest('POST', '/api/auth/ad-login', data);
    return response.json();
  },

  // Get current user
  getCurrentUser: async (): Promise<AuthResponse> => {
    const response = await apiRequest('GET', '/api/me');
    return response.json();
  },

  // Logout
  logout: async (): Promise<{ message: string }> => {
    const response = await apiRequest('POST', '/api/auth/logout');
    return response.json();
  }
};

// Ticket API
export const ticketApi = {
  // Get tickets for current user
  getTickets: async (): Promise<{ tickets: Ticket[] }> => {
    const response = await apiRequest('GET', '/api/tickets');
    return response.json();
  },

  // Create a ticket
  createTicket: async (data: Partial<Ticket>): Promise<{ ticket: Ticket }> => {
